//   originalPurchasePriceDate: "2017-09-03",
//   recentValuation: { amount: 310000, status: "good" },
//   associatedMortgages: [
//     {
//       name: "HSBC Repayment Mortgage",
//       uid: "fb463121-b51a-490d-9f19-d2ea76f05e25",
//       currentBalance: -175000,
//     },
//   ],
//   canBeManaged: false,
//   postcode: "BS1 2AA",
//   lastUpdate: "2020-12-01T08:55:33.421Z",
//   updateAfterDays: 30,
// };



const Detail = () => {
  const [account, setAccount] = useState({});

useEffect(() => {
  axios
    .get("/api/account")
    .then((response) => setAccount(response.account))
    .then(console.log());
}, []);

// checking if account is empty
if (!!account) {
  return (<div></div>)
} 

  console.log(account)

// const Detail = ({}) => {
//   useEffect(() => {
//     window
//       .fetch("/api/account")
//       .then((res) => res.json())
//       .then(console.log);
//   }, []);

  let mortgage;
  const lastUpdate = new Date(account.lastUpdate);
  if (account.associatedMortgages.length) {
    mortgage = account.associatedMortgages[0];
  }

  const recentValuationAmountFormatted = formatAmount(account.recentValuation.amount);
  const currentBalanceFormatted = formatAmount(Math.abs(account.associatedMortgages[0].currentBalance));
  const lastUpdatedFormatted = formatDate(lastUpdate);
  console.log(lastUpdate)
  const originalPurchasePriceDateFormatted = formatDate(account.originalPurchasePriceDateFormatted)
  // const originalPruchasePriceFormatted = formatAmount(originalPurchasePrice)

  return (
    <Inset>
      <AccountSection>
        <AccountLabel>Estimated Value</AccountLabel>
        <AccountHeadline>
          {recentValuationAmountFormatted}
        </AccountHeadline>
        <AccountList>
          <InfoText>
            {`Last updated ${lastUpdatedFormatted}`}
          </InfoText>
          <InfoText>
            {`Next update ${format(add(lastUpdate, { days: account.updateAfterDays }),"do MMM yyyy")}`}
          </InfoText>
        </AccountList>
      </AccountSection>
      <AccountSection>
        <AccountLabel>Property details</AccountLabel>
        <RowContainer>
          <AccountList>
            <InfoText>{account.name}</InfoText>
            <InfoText>{account.bankName}</InfoText>
            <InfoText>{account.postcode}</InfoText>
          </AccountList>
        </RowContainer>
      </AccountSection>
      {mortgage && (
        <AccountSection>
          <AccountLabel>Mortgage</AccountLabel>
          <RowContainer
            // This is a dummy action
            onClick={() => alert("You have navigated to the mortgage page")}
          >
            <AccountList>
              <InfoText>
                {currentBalanceFormatted}
              </InfoText>
              <InfoText>{account.associatedMortgages[0].name}</InfoText>
            </AccountList>
          </RowContainer>
        </AccountSection>
      )}
      <AccountSection>
        <AccountLabel>Valuation Changes</AccountLabel>
        <RowContainer>
          <AccountList>
            <InfoText>
              Purchased for&nbsp;<strong>{formatAmount(account.originalPurchasePrice)}</strong>&nbsp; on the&nbsp;
            {originalPurchasePriceDateFormatted}
            </InfoText>
            <InfoText>
              Since purchase&nbsp;
              <strong>
                {sincePurchased(account)} {sincePurchasedPercentage(account)}
              </strong>
            </InfoText>
            <InfoText>
              Annual Appreciation&nbsp;
              <strong>{annualAppreciation(account)}</strong>
            </InfoText>
          </AccountList>
        </RowContainer>
      </AccountSection>
      <Button
        // This is a dummy action
        onClick={() => alert("You have navigated to the edit account page")}
      >
        Edit account
      </Button>
    </Inset>
  );
};

// Images.propTypes = {
